loginUrl:
// 音色设置，以及链接

function login() {}

function zs(zs) {
java.startBrowserAwait(source.bookSourceUrl+'/static/img/z.png',"喝杯咖啡");
}

function my(my) {
java.startBrowserAwait(source.bookSourceUrl+'/key',"获取密钥");
}

function Map(e) {
    var infomap = source.getLoginInfoMap();
    const value = infomap[e];
    if (!value) {
        java.longToast("请填写密钥");
    }
    return value;
}
var sz = source.getVariable();
var data = [
    ["阅读模式", ""],
    ["多人发音", "&tone_id=51"],
    ["真人发音", "&tone_id=0"],
    ["甜美少女", "&tone_id=1"],
    ["清亮青叔", "&tone_id=2"],
    ["开朗青年", "&tone_id=5"],
    ["温柔淑女", "&tone_id=6"],
]; // 接口数据

// 判断接口是否为空
if (sz == null || sz == "" || isNaN(sz) || sz < 0 || sz > data.length) {
    sz = 0;
    source.setVariable(sz);
    java.toast(data[sz][0]);
} else {
    sz = parseInt(sz); // 将sz转换为整数
}

// 设置保存接口
function set(num) {
    if (num < 0 || num > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "  设置成功\n当前模式：";
    var tips = "";
    var msg = show + data[num][0] + tips;
    java.longToast(msg);
    return source.setVariable(num);
}


// 获取接口显示
function get(sz) {
    if (sz < 0 || sz > data.length) {
        java.longToast("无效的接口编号！");
        return;
    }
    var show = "";
    return java.longToast(show + data[sz][0]);
}

var option = data[sz][1];
option;

