@js:
  var list = [];
  var arr = result.match(/"\/.*?jpg"/g).join(",").replace(/"/g,"").split(",");
  for(x=0;x<arr.length;x++){
  	flag = true;
  	for(i=0;i<list.length;i++){
  		if(arr[x] == list[i]){
  			flag = false;
      }
   }
  		if(flag){
			list.push(arr[x])
    }
}
list.slice(0,list.length-2).map(src=>`<img src="${src}">`).join("\n")

